import tabsStyles from "./tabs.module.css";
import React from "react";
import { Tab } from "@ya.praktikum/react-developer-burger-ui-components";

export default function Tabs({ tabsRef, activeTab, setActiveTab }) {
  const clickTab = (evt) => {
    setActiveTab(evt);
    document.getElementById(evt).scrollIntoView();
  };
  return (
    <div className={tabsStyles.tabs} ref={tabsRef}>
      <div>
        <Tab value="buns" active={activeTab === "buns"} onClick={clickTab}>
          Булки
        </Tab>
      </div>

      <Tab value="sauces" active={activeTab === "sauces"} onClick={clickTab}>
        Соусы
      </Tab>
      <Tab value="main" active={activeTab === "main"} onClick={clickTab}>
        Начинки
      </Tab>
    </div>
  );
}
