import React from "react";
import ingredientDetailsStyles from "./ingredientDetails.module.css";
import IngredientType from "../../utils/types";
import { CloseIcon } from "@ya.praktikum/react-developer-burger-ui-components";
import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import { getAllIngredients } from "../../services/ingredients/selectors";

export default function IngredientDetails() {
  let { id } = useParams();
  const ingredients = useSelector(getAllIngredients);
  const ingredient = ingredients.filter((ingredient) => ingredient._id === id);
  return (
    <React.Fragment>
      <div className={ingredientDetailsStyles.modal}>
        <div className={ingredientDetailsStyles.top}>
          <h2 className="text text_type_main-large">Детали ингредиента</h2>
          <button type="button">
            <CloseIcon type="primary" />
          </button>
        </div>
        <div className={ingredientDetailsStyles.content}>
          <div className={ingredientDetailsStyles.container}>
            <img src={ingredient.image_large} alt={ingredient.name} />
            <h3 className="text text_type_main-medium">{ingredient.name}</h3>
            <ul>
              <li>
                <span className="text text_type_main-default text_color_inactive">Калории,ккал</span>
                <span className="text text_type_digits-default text_color_inactive">{ingredient.calories}</span>
              </li>
              <li>
                <span className="text text_type_main-default text_color_inactive">Белки, г</span>
                <span className="text text_type_digits-default text_color_inactive">{ingredient.proteins}</span>
              </li>
              <li>
                <span className="text text_type_main-default text_color_inactive">Жиры, г</span>
                <span className="text text_type_digits-default text_color_inactive">{ingredient.fat}</span>
              </li>
              <li>
                <span className="text text_type_main-default text_color_inactive">Углеводы, г</span>
                <span className="text text_type_digits-default text_color_inactive">{ingredient.carbohydrates}</span>
              </li>
            </ul>
          </div>
        </div>
      </div>
      <div className={ingredientDetailsStyles.overlay}></div>
    </React.Fragment>
  );
}
IngredientDetails.propTypes = {
  ingredient: IngredientType.isRequired,
};
