import React from "react";
import orderDetailsStyles from "./orderDetails.module.css";
import done from "../../images/done.png";
import PropTypes from "prop-types";

export default function OrderDetails({ orderId }) {
  return (
    <React.Fragment>
      <div className={orderDetailsStyles.modal}>
        <div className={orderDetailsStyles.top}>
          <h2 className="text text_type_main-large">{title}</h2>
          <button type="button" onClick={onClose}>
            <CloseIcon type="primary" />
          </button>
        </div>
        <div className={orderDetailsStyles.content}>
          <div className={orderDetailsStyles.container}>
            <span className="text text_type_digits-large">{orderId}</span>
            <h3 className="text text_type_main-medium">идентификатор заказа</h3>
            <img src={done} alt="Иконка подтверждения" />
            <p className="text text_type_main-default">Ваш заказ начали готовить</p>
            <p className="text text_type_main-default text_color_inactive">Дождитесь готовности на орбитальной станции</p>
          </div>
        </div>
      </div>
      <ModalOverlay onClose={onClose} />
    </React.Fragment>
  );
}
OrderDetails.propTypes = {
  orderId: PropTypes.number.isRequired,
};
