import { data, useNavigate } from "react-router-dom";
import BaseService from "../../utils/BaseService";
export const USER_SET_AUTH_CHECKED = "USER/AUTH_CHEKED";
export const USER_SET = "USER/SET";
export const login = (data) => (dispatch) => {
  return BaseService.login(data).then((res) => {
    dispatch({
      type: USER_SET,
      payload: res.user,
    });
    dispatch({
      type: USER_SET_AUTH_CHECKED,
      payload: true,
    });
    BaseService.setTokens(res.accessToken, res.refreshToken);
  });
};
export const register = (data) => (dispatch) => {
  return BaseService.register(data).then((res) => {
    dispatch({
      type: USER_SET,
      payload: res.user,
    });
    dispatch({
      type: USER_SET_AUTH_CHECKED,
      payload: true,
    });
    BaseService.setTokens(res.accessToken, res.refreshToken);
  });
};
export const checkUserAuth = () => (dispatch) => {
  if (localStorage.getItem("accessToken")) {
    BaseService.getUserData().then((res) => {
      dispatch({
        type: USER_SET,
        payload: res.user,
      });
      dispatch({
        type: USER_SET_AUTH_CHECKED,
        payload: true,
      });
    });
  } else {
    dispatch({
      type: USER_SET_AUTH_CHECKED,
      payload: true,
    });
  }
};
export const logout = (navigate) => (dispatch) => {
  return BaseService.logout().then(() => {
    dispatch({
      type: USER_SET,
      payload: null,
    });
    localStorage.removeItem("accessToken");
    localStorage.removeItem("refreshToken");
    navigate("/");
  });
};
export const updateUserInfo = (data) => (dispatch) => {
  return BaseService.updateUserData(data).then((res) => {
    dispatch({
      type: USER_SET,
      payload: res.user,
    });
  });
};
export const forgotPassword = (data, navigate) => (dispatch) => {
  return BaseService.forgotPassword(data).then((res) => {
    navigate("/reset-password");
  });
};
export const resetPassword = (data, navigate) => (dispatch) => {
  return BaseService.resetPassword(data).then((res) => {
    navigate("/login");
  });
};
