import React, { useEffect, useRef, useState } from "react";
import profileFormStyles from "./profileForm.module.css";
import { Input, Button } from "@ya.praktikum/react-developer-burger-ui-components";
import { useSelector } from "react-redux";
import { getUser } from "../../services/user/selectors";

function ProfileForm() {
  const [icon, setIcon] = useState("ShowIcon");
  const [inputsState, setInputsState] = useState({
    name: { isDisabled: true, icon: "EditIcon" },
    login: { isDisabled: true, icon: "EditIcon" },
    password: { isDisabled: true, icon: "EditIcon" },
  });
  const [showPassword, setShowPassword] = useState(false);
  const user = useSelector(getUser);
  console.log(user);
  const nameRef = useRef(user.name);
  const loginRef = useRef(user.email);
  const passwordRef = useRef(null);
  useEffect(() => {
    if (nameRef.current) nameRef.current.value = user.name || "";
    if (loginRef.current) loginRef.current.value = user.email || "";
    if (passwordRef.current) passwordRef.current.value = "111";
  }, [user]);
  const handleSubmit = (e) => {
    e.preventDefault();
    const name = nameRef.current.value;
    const login = loginRef.current.value;
    const password = passwordRef.current.value;
    console.log(name, password);
  };
  const onIconClick = (e) => {
    setIcon((prevIcon) => {
      return prevIcon === "ShowIcon" ? "HideIcon" : "ShowIcon";
    });
    setShowPassword(!showPassword);
  };
  return (
    <form onSubmit={handleSubmit} className={profileFormStyles.form}>
      <Input type={"text"} placeholder={"Имя"} name={"name"} error={false} ref={nameRef} onIconClick={onIconClick} errorText={"Ошибка"} size={"default"} extraClass="ml-1" icon="EditIcon" disabled />
      <Input
        type={"text"}
        placeholder={"Логин"}
        name={"login"}
        error={false}
        ref={loginRef}
        onIconClick={onIconClick}
        errorText={"Ошибка"}
        size={"default"}
        extraClass="ml-1"
        icon="EditIcon"
        disabled
      />
      <Input
        type="password"
        placeholder={"Пароль"}
        name={"password"}
        error={false}
        ref={passwordRef}
        onIconClick={onIconClick}
        errorText={"Ошибка"}
        size={"default"}
        extraClass="ml-1"
        icon="EditIcon"
        disabled
      />
    </form>
  );
}

export default ProfileForm;
