import tabsStyles from './tabs.module.css';
import React from 'react';
import { Tab } from '@ya.praktikum/react-developer-burger-ui-components';

export default function Tabs({ }) {
    const [current, setCurrent] = React.useState('buns');
    const clickTab = (e) => {
        console.log(e)
        setCurrent('buns'); 
        document.getElementById('buns').scrollIntoView();
    }
    return (
        <div className={tabsStyles.tabs}>
            <Tab value="buns" active={current === 'buns'} onClick={clickTab}>
                Булки
            </Tab>
            <Tab value="sauces" active={current === 'sauces'} onClick={() => { setCurrent('sauces'); document.getElementById('sauces').scrollIntoView(); }}>
                Соусы
            </Tab>
            <Tab value="main" active={current === 'main'} onClick={() => { setCurrent('three'); document.getElementById('main').scrollIntoView(); }}>
                Начинки
            </Tab>
        </div>
    )
}
