import burgerSectionStyles from "./burgerSection.module.css";
import React, { useEffect, useState } from "react";

import BurgerIngredient from "../BurgerIngredient/BurgerIngredient";
import { useSelector } from "react-redux";
import { getConstructorIngredients } from "../../services/constructorIngredients/selectors";
import { useDrag } from "react-dnd";

export default function BurgerSection({ ingredients, id, title, ref }) {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: "ingredient",
    item: { name },
    end: (item, monitor) => {
      const dropResult = monitor.getDropResult();
      if (item && dropResult) {
        alert(`You dropped ${item.name} into ${dropResult.name}!`);
      }
    },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
      handlerId: monitor.getHandlerId(),
    }),
  }));
  return (
    <div ref={ref}>
      <li id={id} className={burgerSectionStyles.item} ref={drag}>
        <h3 className="text text_type_main-medium">{title}</h3>
        <ul>
          {ingredients.map((ingredient) => {
            return <BurgerIngredient ingredient={ingredient} key={ingredient._id} />;
          })}
        </ul>
      </li>
    </div>
  );
}
