import React, { useRef, useState } from "react";
import profileFormStyles from "./profileForm.module.css";
import { Input, Button } from "@ya.praktikum/react-developer-burger-ui-components";

function ProfileForm() {
  const [icon, setIcon] = useState("ShowIcon");
  const [showPassword, setShowPassword] = useState(false);
  const nameRef = useRef(null);
  const loginRef = useRef(null);
  const passwordRef = useRef(null);
  const handleSubmit = (e) => {
    e.preventDefault();
    const name = nameRef.current.value;
    const login = loginRef.current.value;
    const password = passwordRef.current.value;
    console.log(name, password);
  };
  const onIconClick = (e) => {
    setIcon((prevIcon) => {
      return prevIcon === "ShowIcon" ? "HideIcon" : "ShowIcon";
    });
    setShowPassword(!showPassword);
  };
  return (
    <form onSubmit={handleSubmit}>
      <Input
        type={showPassword ? "text" : "password"}
        placeholder={"Имя"}
        //   onChange={(e) => setValue(e.target.value)}
        //   value={value}
        name={"name"}
        error={false}
        ref={nameRef}
        //   onIconClick={onIconClick}
        errorText={"Ошибка"}
        size={"default"}
        extraClass="ml-1"
      />
      <Input
        type={"text"}
        placeholder={"Логин"}
        //   onChange={(e) => setValue(e.target.value)}
        //   value={value}
        name={"login"}
        error={false}
        ref={loginRef}
        //   onIconClick={onIconClick}
        errorText={"Ошибка"}
        size={"default"}
        extraClass="ml-1"
      />
      <Input type={"text"} placeholder={"Пароль"} name={"password"} error={false} ref={passwordRef} onIconClick={onIconClick} errorText={"Ошибка"} size={"default"} extraClass="ml-1" icon={icon} />
    </form>
  );
}

export default ProfileForm;
